import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjAccordion } from './OjAccordion';
export { OjAccordion };

/**
 * Retrieve an instance of [[OjAccordion]].
 * @example
 * ```javascript
 * import { ojAccordion } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojAccordion(driver, By.id('my-oj-accordion'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojAccordion(driver: DriverLike, by: By): Promise<OjAccordion> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjAccordion
  if (!(webEl instanceof OjAccordion)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjAccordion.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojAccordion(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjAccordion.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-accordion', OjAccordion);
