import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjActionCard } from './OjActionCard';
export { OjActionCard };

/**
 * Retrieve an instance of [[OjActionCard]].
 * @example
 * ```javascript
 * import { ojActionCard } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojActionCard(driver, By.id('my-oj-action-card'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojActionCard(driver: DriverLike, by: By): Promise<OjActionCard> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjActionCard
  if (!(webEl instanceof OjActionCard)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjActionCard.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojActionCard(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjActionCard.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-action-card', OjActionCard);
