import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjChart } from './OjChart';
export { OjChart };

/**
 * Retrieve an instance of [[OjChart]].
 * @example
 * ```javascript
 * import { ojChart } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojChart(driver, By.id('my-oj-chart'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojChart(driver: DriverLike, by: By): Promise<OjChart> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjChart
  if (!(webEl instanceof OjChart)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjChart.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojChart(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjChart.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-chart', OjChart);
