import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjCheckboxset } from './OjCheckboxset';
export { OjCheckboxset };

/**
 * Retrieve an instance of [[OjCheckboxset]].
 * @example
 * ```javascript
 * import { ojCheckboxset } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojCheckboxset(driver, By.id('my-oj-checkboxset'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojCheckboxset(driver: DriverLike, by: By): Promise<OjCheckboxset> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjCheckboxset
  if (!(webEl instanceof OjCheckboxset)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjCheckboxset.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojCheckboxset(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjCheckboxset.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-checkboxset', OjCheckboxset);
