import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjCollapsible } from './OjCollapsible';
export { OjCollapsible };

/**
 * Retrieve an instance of [[OjCollapsible]].
 * @example
 * ```javascript
 * import { ojCollapsible } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojCollapsible(driver, By.id('my-oj-collapsible'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojCollapsible(driver: DriverLike, by: By): Promise<OjCollapsible> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjCollapsible
  if (!(webEl instanceof OjCollapsible)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjCollapsible.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojCollapsible(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjCollapsible.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-collapsible', OjCollapsible);
