import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjColorPalette } from './OjColorPalette';
export { OjColorPalette };

/**
 * Retrieve an instance of [[OjColorPalette]].
 * @example
 * ```javascript
 * import { ojColorPalette } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojColorPalette(driver, By.id('my-oj-color-palette'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojColorPalette(driver: DriverLike, by: By): Promise<OjColorPalette> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjColorPalette
  if (!(webEl instanceof OjColorPalette)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjColorPalette.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojColorPalette(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjColorPalette.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-color-palette', OjColorPalette);
