import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjColorSpectrum } from './OjColorSpectrum';
export { OjColorSpectrum };

/**
 * Retrieve an instance of [[OjColorSpectrum]].
 * @example
 * ```javascript
 * import { ojColorSpectrum } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojColorSpectrum(driver, By.id('my-oj-color-spectrum'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojColorSpectrum(driver: DriverLike, by: By): Promise<OjColorSpectrum> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjColorSpectrum
  if (!(webEl instanceof OjColorSpectrum)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjColorSpectrum.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojColorSpectrum(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjColorSpectrum.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-color-spectrum', OjColorSpectrum);
