import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjComboboxMany } from './OjComboboxMany';
export { OjComboboxMany };

/**
 * Retrieve an instance of [[OjComboboxMany]].
 * @example
 * ```javascript
 * import { ojComboboxMany } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojComboboxMany(driver, By.id('my-oj-combobox-many'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojComboboxMany(driver: DriverLike, by: By): Promise<OjComboboxMany> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjComboboxMany
  if (!(webEl instanceof OjComboboxMany)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjComboboxMany.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojComboboxMany(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjComboboxMany.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-combobox-many', OjComboboxMany);
