import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjComboboxOne } from './OjComboboxOne';
export { OjComboboxOne };

/**
 * Retrieve an instance of [[OjComboboxOne]].
 * @example
 * ```javascript
 * import { ojComboboxOne } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojComboboxOne(driver, By.id('my-oj-combobox-one'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojComboboxOne(driver: DriverLike, by: By): Promise<OjComboboxOne> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjComboboxOne
  if (!(webEl instanceof OjComboboxOne)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjComboboxOne.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojComboboxOne(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjComboboxOne.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-combobox-one', OjComboboxOne);
