import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjConveyorBelt } from './OjConveyorBelt';
export { OjConveyorBelt };

/**
 * Retrieve an instance of [[OjConveyorBelt]].
 * @example
 * ```javascript
 * import { ojConveyorBelt } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojConveyorBelt(driver, By.id('my-oj-conveyor-belt'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojConveyorBelt(driver: DriverLike, by: By): Promise<OjConveyorBelt> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjConveyorBelt
  if (!(webEl instanceof OjConveyorBelt)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjConveyorBelt.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojConveyorBelt(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjConveyorBelt.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-conveyor-belt', OjConveyorBelt);
