import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjDataGrid } from './OjDataGrid';
export { OjDataGrid };

/**
 * Retrieve an instance of [[OjDataGrid]].
 * @example
 * ```javascript
 * import { ojDataGrid } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojDataGrid(driver, By.id('my-oj-data-grid'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojDataGrid(driver: DriverLike, by: By): Promise<OjDataGrid> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjDataGrid
  if (!(webEl instanceof OjDataGrid)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjDataGrid.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojDataGrid(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjDataGrid.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-data-grid', OjDataGrid);
