import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjDateTimePicker } from './OjDateTimePicker';
export { OjDateTimePicker };

/**
 * Retrieve an instance of [[OjDateTimePicker]].
 * @example
 * ```javascript
 * import { ojDateTimePicker } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojDateTimePicker(driver, By.id('my-oj-date-time-picker'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojDateTimePicker(driver: DriverLike, by: By): Promise<OjDateTimePicker> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjDateTimePicker
  if (!(webEl instanceof OjDateTimePicker)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjDateTimePicker.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojDateTimePicker(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjDateTimePicker.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-date-time-picker', OjDateTimePicker);
