import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjDiagram } from './OjDiagram';
export { OjDiagram };

/**
 * Retrieve an instance of [[OjDiagram]].
 * @example
 * ```javascript
 * import { ojDiagram } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojDiagram(driver, By.id('my-oj-diagram'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojDiagram(driver: DriverLike, by: By): Promise<OjDiagram> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjDiagram
  if (!(webEl instanceof OjDiagram)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjDiagram.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojDiagram(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjDiagram.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-diagram', OjDiagram);
