import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjDialog } from './OjDialog';
export { OjDialog };

/**
 * Retrieve an instance of [[OjDialog]].
 * @example
 * ```javascript
 * import { ojDialog } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojDialog(driver, By.id('my-oj-dialog'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojDialog(driver: DriverLike, by: By): Promise<OjDialog> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjDialog
  if (!(webEl instanceof OjDialog)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjDialog.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojDialog(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjDialog.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-dialog', OjDialog);
