import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjDrawerLayout } from './OjDrawerLayout';
export { OjDrawerLayout };

/**
 * Retrieve an instance of [[OjDrawerLayout]].
 * @example
 * ```javascript
 * import { ojDrawerLayout } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojDrawerLayout(driver, By.id('my-oj-drawer-layout'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojDrawerLayout(driver: DriverLike, by: By): Promise<OjDrawerLayout> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjDrawerLayout
  if (!(webEl instanceof OjDrawerLayout)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjDrawerLayout.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojDrawerLayout(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjDrawerLayout.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-drawer-layout', OjDrawerLayout);
