import { OjDrawerPopupBase } from './OjDrawerPopupBase';

/**
 * The component WebElement for [oj-drawer-popup](../../jsdocs/oj.ojDrawerPopup.html).
 * Do not instantiate this class directly, instead, use
 * [ojDrawerPopup](../modules/elements.html#ojDrawerPopup).
 */
export class OjDrawerPopup extends OjDrawerPopupBase {
  /**
   * Dismiss the drawer
   */
  async doClose(): Promise<void> {
    return this.setProperty<boolean>('opened', false);
  }
}
