import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjFilmStrip } from './OjFilmStrip';
export { OjFilmStrip };

/**
 * Retrieve an instance of [[OjFilmStrip]].
 * @example
 * ```javascript
 * import { ojFilmStrip } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojFilmStrip(driver, By.id('my-oj-film-strip'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojFilmStrip(driver: DriverLike, by: By): Promise<OjFilmStrip> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjFilmStrip
  if (!(webEl instanceof OjFilmStrip)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjFilmStrip.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojFilmStrip(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjFilmStrip.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-film-strip', OjFilmStrip);
