import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjIndexer } from './OjIndexer';
export { OjIndexer };

/**
 * Retrieve an instance of [[OjIndexer]].
 * @example
 * ```javascript
 * import { ojIndexer } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojIndexer(driver, By.id('my-oj-indexer'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojIndexer(driver: DriverLike, by: By): Promise<OjIndexer> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjIndexer
  if (!(webEl instanceof OjIndexer)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjIndexer.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojIndexer(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjIndexer.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-indexer', OjIndexer);
