import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjInputDate } from './OjInputDate';
export { OjInputDate };

/**
 * Retrieve an instance of [[OjInputDate]].
 * @example
 * ```javascript
 * import { ojInputDate } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojInputDate(driver, By.id('my-oj-input-date'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojInputDate(driver: DriverLike, by: By): Promise<OjInputDate> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjInputDate
  if (!(webEl instanceof OjInputDate)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjInputDate.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojInputDate(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjInputDate.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-input-date', OjInputDate);
