import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjInputNumber } from './OjInputNumber';
export { OjInputNumber };

/**
 * Retrieve an instance of [[OjInputNumber]].
 * @example
 * ```javascript
 * import { ojInputNumber } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojInputNumber(driver, By.id('my-oj-input-number'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojInputNumber(driver: DriverLike, by: By): Promise<OjInputNumber> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjInputNumber
  if (!(webEl instanceof OjInputNumber)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjInputNumber.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojInputNumber(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjInputNumber.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-input-number', OjInputNumber);
