import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjInputPassword } from './OjInputPassword';
export { OjInputPassword };

/**
 * Retrieve an instance of [[OjInputPassword]].
 * @example
 * ```javascript
 * import { ojInputPassword } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojInputPassword(driver, By.id('my-oj-input-password'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojInputPassword(driver: DriverLike, by: By): Promise<OjInputPassword> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjInputPassword
  if (!(webEl instanceof OjInputPassword)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjInputPassword.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojInputPassword(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjInputPassword.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-input-password', OjInputPassword);
