import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjInputSearch } from './OjInputSearch';
export { OjInputSearch };

/**
 * Retrieve an instance of [[OjInputSearch]].
 * @example
 * ```javascript
 * import { ojInputSearch } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojInputSearch(driver, By.id('my-oj-input-search'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojInputSearch(driver: DriverLike, by: By): Promise<OjInputSearch> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjInputSearch
  if (!(webEl instanceof OjInputSearch)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjInputSearch.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojInputSearch(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjInputSearch.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-input-search', OjInputSearch);
