import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjInputText } from './OjInputText';
export { OjInputText };

/**
 * Retrieve an instance of [[OjInputText]].
 * @example
 * ```javascript
 * import { ojInputText } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojInputText(driver, By.id('my-oj-input-text'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojInputText(driver: DriverLike, by: By): Promise<OjInputText> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjInputText
  if (!(webEl instanceof OjInputText)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjInputText.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojInputText(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjInputText.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-input-text', OjInputText);
