import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjLabel } from './OjLabel';
export { OjLabel };

/**
 * Retrieve an instance of [[OjLabel]].
 * @example
 * ```javascript
 * import { ojLabel } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojLabel(driver, By.id('my-oj-label'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojLabel(driver: DriverLike, by: By): Promise<OjLabel> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjLabel
  if (!(webEl instanceof OjLabel)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjLabel.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojLabel(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjLabel.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-label', OjLabel);
