import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjLegend } from './OjLegend';
export { OjLegend };

/**
 * Retrieve an instance of [[OjLegend]].
 * @example
 * ```javascript
 * import { ojLegend } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojLegend(driver, By.id('my-oj-legend'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojLegend(driver: DriverLike, by: By): Promise<OjLegend> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjLegend
  if (!(webEl instanceof OjLegend)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjLegend.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojLegend(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjLegend.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-legend', OjLegend);
