import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjMasonryLayout } from './OjMasonryLayout';
export { OjMasonryLayout };

/**
 * Retrieve an instance of [[OjMasonryLayout]].
 * @example
 * ```javascript
 * import { ojMasonryLayout } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojMasonryLayout(driver, By.id('my-oj-masonry-layout'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojMasonryLayout(driver: DriverLike, by: By): Promise<OjMasonryLayout> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjMasonryLayout
  if (!(webEl instanceof OjMasonryLayout)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjMasonryLayout.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojMasonryLayout(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjMasonryLayout.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-masonry-layout', OjMasonryLayout);
