import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjMenuButton } from './OjMenuButton';
export { OjMenuButton };

/**
 * Retrieve an instance of [[OjMenuButton]].
 * @example
 * ```javascript
 * import { ojMenuButton } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojMenuButton(driver, By.id('my-oj-menu-button'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojMenuButton(driver: DriverLike, by: By): Promise<OjMenuButton> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjMenuButton
  if (!(webEl instanceof OjMenuButton)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjMenuButton.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojMenuButton(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjMenuButton.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-menu-button', OjMenuButton);
