import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjMenu } from './OjMenu';
export { OjMenu };

/**
 * Retrieve an instance of [[OjMenu]].
 * @example
 * ```javascript
 * import { ojMenu } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojMenu(driver, By.id('my-oj-menu'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojMenu(driver: DriverLike, by: By): Promise<OjMenu> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjMenu
  if (!(webEl instanceof OjMenu)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjMenu.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojMenu(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjMenu.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-menu', OjMenu);
