import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjMessageBanner } from './OjMessageBanner';
export { OjMessageBanner };

/**
 * Retrieve an instance of [[OjMessageBanner]].
 * @example
 * ```javascript
 * import { ojMessageBanner } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojMessageBanner(driver, By.id('my-oj-message-banner'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojMessageBanner(driver: DriverLike, by: By): Promise<OjMessageBanner> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjMessageBanner
  if (!(webEl instanceof OjMessageBanner)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjMessageBanner.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojMessageBanner(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjMessageBanner.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-message-banner', OjMessageBanner);
