import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjMessage } from './OjMessage';
export { OjMessage };

/**
 * Retrieve an instance of [[OjMessage]].
 * @example
 * ```javascript
 * import { ojMessage } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojMessage(driver, By.id('my-oj-message'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojMessage(driver: DriverLike, by: By): Promise<OjMessage> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjMessage
  if (!(webEl instanceof OjMessage)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjMessage.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojMessage(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjMessage.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-message', OjMessage);
