import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjNBox } from './OjNBox';
export { OjNBox };

/**
 * Retrieve an instance of [[OjNBox]].
 * @example
 * ```javascript
 * import { ojNBox } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojNBox(driver, By.id('my-oj-n-box'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojNBox(driver: DriverLike, by: By): Promise<OjNBox> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjNBox
  if (!(webEl instanceof OjNBox)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjNBox.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojNBox(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjNBox.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-n-box', OjNBox);
