import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjNavigationList } from './OjNavigationList';
export { OjNavigationList };

/**
 * Retrieve an instance of [[OjNavigationList]].
 * @example
 * ```javascript
 * import { ojNavigationList } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojNavigationList(driver, By.id('my-oj-navigation-list'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojNavigationList(driver: DriverLike, by: By): Promise<OjNavigationList> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjNavigationList
  if (!(webEl instanceof OjNavigationList)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjNavigationList.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojNavigationList(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjNavigationList.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-navigation-list', OjNavigationList);
