import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjPagingControl } from './OjPagingControl';
export { OjPagingControl };

/**
 * Retrieve an instance of [[OjPagingControl]].
 * @example
 * ```javascript
 * import { ojPagingControl } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojPagingControl(driver, By.id('my-oj-paging-control'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojPagingControl(driver: DriverLike, by: By): Promise<OjPagingControl> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjPagingControl
  if (!(webEl instanceof OjPagingControl)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjPagingControl.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojPagingControl(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjPagingControl.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-paging-control', OjPagingControl);
