import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjPictoChart } from './OjPictoChart';
export { OjPictoChart };

/**
 * Retrieve an instance of [[OjPictoChart]].
 * @example
 * ```javascript
 * import { ojPictoChart } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojPictoChart(driver, By.id('my-oj-picto-chart'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojPictoChart(driver: DriverLike, by: By): Promise<OjPictoChart> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjPictoChart
  if (!(webEl instanceof OjPictoChart)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjPictoChart.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojPictoChart(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjPictoChart.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-picto-chart', OjPictoChart);
