import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjPopup } from './OjPopup';
export { OjPopup };

/**
 * Retrieve an instance of [[OjPopup]].
 * @example
 * ```javascript
 * import { ojPopup } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojPopup(driver, By.id('my-oj-popup'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojPopup(driver: DriverLike, by: By): Promise<OjPopup> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjPopup
  if (!(webEl instanceof OjPopup)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjPopup.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojPopup(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjPopup.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-popup', OjPopup);
