import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjProgressList } from './OjProgressList';
export { OjProgressList };

/**
 * Retrieve an instance of [[OjProgressList]].
 * @example
 * ```javascript
 * import { ojProgressList } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojProgressList(driver, By.id('my-oj-progress-list'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojProgressList(driver: DriverLike, by: By): Promise<OjProgressList> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjProgressList
  if (!(webEl instanceof OjProgressList)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjProgressList.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojProgressList(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjProgressList.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-progress-list', OjProgressList);
