import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjProgress } from './OjProgress';
export { OjProgress };

/**
 * Retrieve an instance of [[OjProgress]].
 * @example
 * ```javascript
 * import { ojProgress } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojProgress(driver, By.id('my-oj-progress'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojProgress(driver: DriverLike, by: By): Promise<OjProgress> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjProgress
  if (!(webEl instanceof OjProgress)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjProgress.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojProgress(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjProgress.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-progress', OjProgress);
