import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjRadioset } from './OjRadioset';
export { OjRadioset };

/**
 * Retrieve an instance of [[OjRadioset]].
 * @example
 * ```javascript
 * import { ojRadioset } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojRadioset(driver, By.id('my-oj-radioset'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojRadioset(driver: DriverLike, by: By): Promise<OjRadioset> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjRadioset
  if (!(webEl instanceof OjRadioset)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjRadioset.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojRadioset(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjRadioset.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-radioset', OjRadioset);
