import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjRatingGauge } from './OjRatingGauge';
export { OjRatingGauge };

/**
 * Retrieve an instance of [[OjRatingGauge]].
 * @example
 * ```javascript
 * import { ojRatingGauge } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojRatingGauge(driver, By.id('my-oj-rating-gauge'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojRatingGauge(driver: DriverLike, by: By): Promise<OjRatingGauge> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjRatingGauge
  if (!(webEl instanceof OjRatingGauge)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjRatingGauge.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojRatingGauge(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjRatingGauge.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-rating-gauge', OjRatingGauge);
