import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjRowExpander } from './OjRowExpander';
export { OjRowExpander };

/**
 * Retrieve an instance of [[OjRowExpander]].
 * @example
 * ```javascript
 * import { ojRowExpander } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojRowExpander(driver, By.id('my-oj-row-expander'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojRowExpander(driver: DriverLike, by: By): Promise<OjRowExpander> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjRowExpander
  if (!(webEl instanceof OjRowExpander)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjRowExpander.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojRowExpander(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjRowExpander.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-row-expander', OjRowExpander);
