import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjSelectMany } from './OjSelectMany';
export { OjSelectMany };

/**
 * Retrieve an instance of [[OjSelectMany]].
 * @example
 * ```javascript
 * import { ojSelectMany } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojSelectMany(driver, By.id('my-oj-select-many'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojSelectMany(driver: DriverLike, by: By): Promise<OjSelectMany> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjSelectMany
  if (!(webEl instanceof OjSelectMany)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjSelectMany.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojSelectMany(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjSelectMany.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-select-many', OjSelectMany);
