import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjSelectOne } from './OjSelectOne';
export { OjSelectOne };

/**
 * Retrieve an instance of [[OjSelectOne]].
 * @example
 * ```javascript
 * import { ojSelectOne } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojSelectOne(driver, By.id('my-oj-select-one'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojSelectOne(driver: DriverLike, by: By): Promise<OjSelectOne> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjSelectOne
  if (!(webEl instanceof OjSelectOne)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjSelectOne.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojSelectOne(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjSelectOne.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-select-one', OjSelectOne);
