import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjSelectSingle } from './OjSelectSingle';
export { OjSelectSingle };

/**
 * Retrieve an instance of [[OjSelectSingle]].
 * @example
 * ```javascript
 * import { ojSelectSingle } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojSelectSingle(driver, By.id('my-oj-select-single'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojSelectSingle(driver: DriverLike, by: By): Promise<OjSelectSingle> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjSelectSingle
  if (!(webEl instanceof OjSelectSingle)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjSelectSingle.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojSelectSingle(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjSelectSingle.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-select-single', OjSelectSingle);
