import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjSelector } from './OjSelector';
export { OjSelector };

/**
 * Retrieve an instance of [[OjSelector]].
 * @example
 * ```javascript
 * import { ojSelector } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojSelector(driver, By.id('my-oj-selector'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojSelector(driver: DriverLike, by: By): Promise<OjSelector> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjSelector
  if (!(webEl instanceof OjSelector)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjSelector.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojSelector(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjSelector.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-selector', OjSelector);
