import { OjSliderBase } from './OjSliderBase';

/**
 * The component WebElement for [oj-slider](../../jsdocs/oj.ojSlider.html).
 * Do not instantiate this class directly, instead, use
 * [ojSlider](../modules/elements.html#ojSlider).
 */
export class OjSlider extends OjSliderBase {
  /**
   * Sets the value of "value" property.
   * The numerical value of the slider.
   * @param value The value to set for "value"
   */
  async changeValue(value: number) {
    // Call focus() on the root element
    await this.getDriver().executeScript('arguments[0].focus()', this);
    // Only mutate if not disabled
    const disabled = await this.getDisabled();
    if (!disabled) {
      return this.setProperty<number>('value', value);
    }
  }
}
