import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjSlider } from './OjSlider';
export { OjSlider };

/**
 * Retrieve an instance of [[OjSlider]].
 * @example
 * ```javascript
 * import { ojSlider } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojSlider(driver, By.id('my-oj-slider'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojSlider(driver: DriverLike, by: By): Promise<OjSlider> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjSlider
  if (!(webEl instanceof OjSlider)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjSlider.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojSlider(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjSlider.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-slider', OjSlider);
