import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjSparkChart } from './OjSparkChart';
export { OjSparkChart };

/**
 * Retrieve an instance of [[OjSparkChart]].
 * @example
 * ```javascript
 * import { ojSparkChart } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojSparkChart(driver, By.id('my-oj-spark-chart'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojSparkChart(driver: DriverLike, by: By): Promise<OjSparkChart> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjSparkChart
  if (!(webEl instanceof OjSparkChart)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjSparkChart.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojSparkChart(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjSparkChart.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-spark-chart', OjSparkChart);
