import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjStatusMeterGauge } from './OjStatusMeterGauge';
export { OjStatusMeterGauge };

/**
 * Retrieve an instance of [[OjStatusMeterGauge]].
 * @example
 * ```javascript
 * import { ojStatusMeterGauge } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojStatusMeterGauge(driver, By.id('my-oj-status-meter-gauge'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojStatusMeterGauge(driver: DriverLike, by: By): Promise<OjStatusMeterGauge> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjStatusMeterGauge
  if (!(webEl instanceof OjStatusMeterGauge)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjStatusMeterGauge.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojStatusMeterGauge(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjStatusMeterGauge.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-status-meter-gauge', OjStatusMeterGauge);
