import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjStreamList } from './OjStreamList';
export { OjStreamList };

/**
 * Retrieve an instance of [[OjStreamList]].
 * @example
 * ```javascript
 * import { ojStreamList } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojStreamList(driver, By.id('my-oj-stream-list'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojStreamList(driver: DriverLike, by: By): Promise<OjStreamList> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjStreamList
  if (!(webEl instanceof OjStreamList)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjStreamList.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojStreamList(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjStreamList.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-stream-list', OjStreamList);
