import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjSunburst } from './OjSunburst';
export { OjSunburst };

/**
 * Retrieve an instance of [[OjSunburst]].
 * @example
 * ```javascript
 * import { ojSunburst } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojSunburst(driver, By.id('my-oj-sunburst'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojSunburst(driver: DriverLike, by: By): Promise<OjSunburst> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjSunburst
  if (!(webEl instanceof OjSunburst)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjSunburst.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojSunburst(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjSunburst.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-sunburst', OjSunburst);
